examples.typescript:
import { Rule } from "@rulebricks/sdk/forge";

// Create a standalone rule
const rule = new Rule();

// Or set workspace later
rule.setWorkspace(client);

const age = rule.addNumberField("age", "Customer age");
const status = rule.addStringField("status", "Account status");
rule.addStringResponse("tier", "Customer tier");

// Define a condition
rule.when({
    age: age.greater_than(18),
    status: status.equals("active")
}).then({
    tier: "premium"
});

// If ANY of these conditions match, apply the response
rule.any({
    age: age.greater_than(65),
    status: status.equals("vip")
}).then({
    discount_percent: 20
});

// Find all conditions where age > 18
const ageField = rule.getNumberField("age");
const matches = rule.findConditions({
    age: ageField.greater_than(18)
});

// Modify the first match
if (matches.length > 0) {
    matches[0].then({ tier: "gold" });
}

import { RuleTest } from "@rulebricks/sdk/forge";

const test = new RuleTest()
    .setName("Adult premium test")
    .expect(
        { age: 25, status: "active" },
        { tier: "premium" }
    )
    .isCritical(true);

rule.addTest(test);

// Requires workspace client
rule.setWorkspace(client);

// Push changes to workspace (draft mode)
await rule.update();

// Requires workspace client
rule.setWorkspace(client);

// Publish the rule (makes it live)
await rule.publish();

// From JSON string
const rule = Rule.fromJSON('{"name": "My Rule", ...}');

// From object
const rule = Rule.fromJSON({ name: "My Rule", conditions: [] });

const rule = new Rule();
rule.setWorkspace(client);
await rule.fromWorkspace("a1b2c3d4-e5f6-7890-abcd-ef1234567890");

const condition = rule.getCondition(0);
condition.when({
    age: age.greater_than(21)
});

// For a new condition (returns Rule)
rule.when({ age: age.greater_than(18) }).then({
    approved: true,
    message: "Welcome!"
});

// For editing existing (returns Condition)
const condition = rule.getCondition(0);
condition.then({ approved: false });

// Get a dynamic value reference
const maxDiscount = await DynamicValues.get("max_discount");

// Use in conditions
rule.when({
    discount: discount.less_than(maxDiscount)
}).then({
    approved: true
});

