    context.Background(),
    &rulebricks.CreateContextRequest{
        Name: "Customer",
        Slug: rulebricks.String("customer"),
        Description: rulebricks.String("Customer entity"),
        Schema: []rulebricks.SchemaItem{
            {Key: "email", Name: "Email", Type: "string"},
            {Key: "age", Name: "Age", Type: "number"},
        },
        TtlSeconds: rulebricks.Int(86400),
        HistoryLimit: rulebricks.Int(100),
    },
)

