initialization.typescript:
import { RulebricksClient } from "@rulebricks/sdk";

const client = new RulebricksClient({
    apiKey: "YOUR_API_KEY",
});

examples.typescript:
await client.rules.solve({
    slug: "my-rule-slug",
    body: {
        name: "Alice Johnson",
        age: 28,
        email: "alice.johnson@example.com"
    }
})

examples.go:
response, err := client.Rules.Solve(
    context.Background(),
    &sdk.SolveRulesRequest{
        Slug: "my-rule-slug",
        Body: map[string]interface{}{
            "name": "Alice Johnson",
            "age": 28,
            "email": "alice.johnson@example.com",
        },
    },
)

examples.csharp:
var requestData = new Dictionary<string, object>
{
    { "name", "Alice Johnson" },
    { "age", 28 },
    { "email", "alice.johnson@example.com" }
};

var result = await client.Rules.SolveAsync(
    "my-rule-slug", requestData);

examples.java:
Map<String, Object> requestData = new HashMap<>();
requestData.put("name", "Alice Johnson");
requestData.put("age", 28);
requestData.put("email", "alice@example.com");

var result = client.rules().solve(
    "my-rule-slug", requestData);

examples.typescript:
await client.rules.bulkSolve({
    slug: "my-rule-slug",
    body: [
        { name: "John Doe", age: 30 },
        { name: "Jane Doe", age: 28 }
    ]
})

examples.go:
responses, err := client.Rules.BulkSolve(
    context.Background(),
    &sdk.BulkSolveRulesRequest{
        Slug: "my-rule-slug",
        Body: []map[string]interface{}{
            {"name": "John Doe", "age": 30},
            {"name": "Jane Doe", "age": 28},
        },
    },
)

examples.csharp:
